INT32   acfg_cust_odm_current_default_hdr_panel_nits(UINT16 *pui2_val)
{
    INT32 i4_ret = 0;
    UINT16   ui2_idx = 0;
    INT16    i2_val = 0;
    UINT32 ui4_model_idx = 0;
    UINT16  ui2_tbl_size = 0;
    
    i4_ret = a_cfg_av_get(CFG_MAKE_ID(APP_CFG_GRPID_VIDEO, APP_CFG_RECID_CLR_TEMP), &i2_val);
    ACFG_LOG_ON_FAIL(i4_ret);

    i4_ret = a_cfg_custom_get_model_idx(&ui4_model_idx);
    ACFG_LOG_ON_FAIL(i4_ret);

    ui2_tbl_size = sizeof (ui2_TPV_panel_nits) / sizeof (ui2_TPV_panel_nits[0]);

    if((ui4_model_idx+1) > ui2_tbl_size)
    {
        DBG_LOG_PRINT(("\n[TPV_FAC] model index(%d) over panel nit table size(%d) ,Line: %d\n", ui4_model_idx, ui2_tbl_size, __LINE__));
        ui2_idx = 300;

        *pui2_val = ui2_idx;
        return i4_ret;
    }
    
    if(i2_val == ACFG_CUST_CLR_TEMP_WARM)
    {
        ui2_idx = ui2_TPV_panel_nits[ui4_model_idx][0];
    }
    else if (i2_val == ACFG_CUST_CLR_TEMP_COOL)
    {
        ui2_idx = ui2_TPV_panel_nits[ui4_model_idx][1];
    }
    else // if (i2_val == ACFG_CUST_CLR_TEMP_NORMAL)
    {
        ui2_idx = ui2_TPV_panel_nits[ui4_model_idx][2];
    }
    
    *pui2_val = ui2_idx;

    return i4_ret;
}

